import React from 'react';
import './index.scss';

function App() {
  const [caunt, setCount] = React.useState(0);
  const onClickPlus = () => {
    setCount(caunt + 1);
  };

  return (
    <div className="App">
      <div>
        <h2>Счетчик:</h2>
        <h1>{caunt}</h1>
        <button onClick={caunt} className="minus">
          - Минус
        </button>
        <button onClick={onClickPlus} className="plus">
          Плюс +
        </button>
      </div>
    </div>
  );
}

export default App;
